mapper.select:
SELECT *
FROM tbl_reply
WHERE bno = #{bno}
ORDER BY rno DESC
LIMIT #{criteria.pageStart}, #{criteria.perPageNum}

SELECT COUNT(bno)
FROM tbl_reply
WHERE bno = #{bno}

mapper.insert:
INSERT INTO tbl_reply (
    bno
    , replytext
    , replyer
) VALUES (
    #{bno}
    , #{replytext}
    , #{replyer}
)

mapper.update:
UPDATE tbl_reply
SET
    replytext = #{replytext}
    , updatedate = NOW()
WHERE rno = #{rno}
