mapper.insert:
INSERT INTO tbl_user (
    uid
    , upw
    , uname
    , uemail
) VALUES (
    #{uid}
    , #{upw}
    , #{uname}
    , #{uemail}
)

mapper.update:
UPDATE tbl_user
SET
    uname = #{uname}
    , uemail =  #{uemail}
WHERE uid = #{uid}

UPDATE tbl_user
SET
    upw = #{upw}
WHERE uid = #{uid}

UPDATE tbl_user
SET logdate = NOW()
WHERE uid = #{uid}

UPDATE tbl_user
SET
    sessionkey     = #{sessionId}
    , sessionlimit = #{next}
WHERE uid = #{uid}
